changeSet.createProcedure:
CREATE OR REPLACE FUNCTION GET_CONTEXT_ID(text)
RETURNS BIGINT AS $result$
    DECLARE result BIGINT;
    BEGIN
        select ID
        into result
        from ALERT.CONFIG_CONTEXTS where CONTEXT = $1;
        RETURN result;
    END;
$result$
LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION GET_FIELD_ID(text)
RETURNS BIGINT AS $result$
    DECLARE result BIGINT;
    BEGIN
        select ID
        into result from ALERT.DEFINED_FIELDS
        where SOURCE_KEY = $1;
        RETURN result;
    END;
$result$
LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION GET_DESCRIPTOR_TYPE_ID(text)
RETURNS BIGINT AS $result$
    DECLARE result BIGINT;
    BEGIN
        select ID
        into result
        from ALERT.DESCRIPTOR_TYPES where TYPE = $1;
        RETURN result;
    END;
$result$
LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION GET_DESCRIPTOR_ID(text)
RETURNS BIGINT AS $result$
    DECLARE result BIGINT;
    BEGIN
        select ID
        into result
        from ALERT.REGISTERED_DESCRIPTORS where NAME = $1;
    RETURN result;
    END;
$result$
LANGUAGE plpgsql;

changeSet.sql:
insert into ALERT.CONFIG_CONTEXTS (CONTEXT)
select ('GLOBAL')
where not exists (
    select * from ALERT.CONFIG_CONTEXTS where CONTEXT = 'GLOBAL'
);

insert into ALERT.CONFIG_CONTEXTS (CONTEXT)
select ('DISTRIBUTION')
where not exists (
    select * from ALERT.CONFIG_CONTEXTS where CONTEXT = 'DISTRIBUTION'
);

insert into ALERT.DESCRIPTOR_TYPES (TYPE)
select ('CHANNEL')
where not exists (
    select * from ALERT.DESCRIPTOR_TYPES where type = 'CHANNEL'
);

insert into ALERT.DESCRIPTOR_TYPES (TYPE)
select ('PROVIDER')
